module.exports = function (restClient) {
  let module = {};
  const urlPrefix = 'subscription/api/';
  let url = urlPrefix;
  function getResponse(data){
    if(data.code === 200){
      return data.result;
    }
    return false;
  }
  module.meta = () => {
    url += `meta`;
    return restClient.get(url).then((data)=> {
      return getResponse(data);
    });
  }
  module.cart_delivery = (body) => {
    url += `cart/delivery`;
    return restClient.post(url, { body }).then((data)=> {
      return getResponse(data);
    });
  }
  module.paymentmethod_update = (body) => {
    url += `paymentmethod/update`;
    return restClient.post(url, { body }).then((data)=> {
      return getResponse(data);
    });
  }
  module.get = (body) => {
    url += `get`;
    return restClient.post(url, { body }).then((data)=> {
      return getResponse(data);
    });
  }
  module.delete = (body) => {
    url += `delete`;
    return restClient.post(url, { body }).then((data)=> {
      return getResponse(data);
    });
  }
  module.delivery = (body) => {
    url += `delivery`;
    return restClient.post(url, { body }).then((data)=> {
      return getResponse(data);
    });
  }
  module.update = (body) => {
    url += `update`;
    return restClient.post(url, { body }).then((data)=> {
      return getResponse(data);
    });
  }
  return module;
}
